/**
 * Get default permissions for a role
 */
export function getDefaultPermissions(role) {
    return DEFAULT_PERMISSIONS[role] || DEFAULT_PERMISSIONS['USER'];
}
/**
 * Check if user has a specific permission
 */
export function hasPermission(userPermissions, permission) {
    return userPermissions[permission] === true;
}
/**
 * Merge custom permissions with default role permissions
 */
export function mergePermissions(role, customPermissions) {
    const defaultPermissions = getDefaultPermissions(role);
    return { ...defaultPermissions, ...customPermissions };
}
